function navBehavior(){const e=document.querySelector(".hamburguer");e.addEventListener("click",()=>{e.classList.toggle("activo")})}function scrollNav(){const e=document.querySelector(".seccion4"),o=document.querySelector(".seccion5"),c=document.querySelector(".seccion6"),t=document.querySelector(".seccion7");document.querySelectorAll(".servicio").forEach(n=>{n.addEventListener("click",()=>{const r=n.id;"mina"===r?e.scrollIntoView({behavior:"smooth"}):"geologia"===r?o.scrollIntoView({behavior:"smooth"}):"ambiente"===r?c.scrollIntoView({behavior:"smooth"}):"geografia"===r&&t.scrollIntoView({behavior:"smooth"})})})}document.addEventListener("DOMContentLoaded",(function(e){navBehavior(),scrollNav()}));